var otpCount = 1;

function generateRinCashOTP(page, email, pin, walletId, walletMobileNo, reGenerateOtpLinkCaption, walletOtpInputCaption) {
    //alert("generateRinCashOTP : "+walletOtpInputCaption);
    var otpConfirmationMessage = "";
    if (document.getElementById("otpOrPin") != null) {
      /*  document.getElementById("rinCashPinDiv").style.display = "none";*/
      document.getElementById("otpOrPin").value = "OTP";
        document.getElementById("newPin").value = "";
        //alert("newPin : "+document.getElementById("newPin").type);
        document.getElementById("newPin").type = "TEXT";
        //alert(document.getElementById("newPin").type);
    }
    /*if (document.getElementById("rinCashOtpDiv") != null) {
        document.getElementById("rinCashOtpDiv").style.display = "block";
    }*/
    document.getElementById("generateOtpLinkCaption").innerHTML = reGenerateOtpLinkCaption;
    $('#newPin').attr('placeholder',walletOtpInputCaption);
    //document.getElementById("pin").placeHolder = walletOtpInputCaption;
    /*if (document.getElementById("generateOtpLinkCaption").style.display == "block") {
        *//*document.getElementById("reGenerateOtpLinkCaption").style.display = "block";*//*
    }*/

    var requestParam = '?emailID=' + email + '&transType=TRANSACTION&pin=' + pin + '&walletId=' + walletId + '&agentCode=' + walletMobileNo + '&otpCount=' + otpCount + '&page=' + page;
    //alert("requestParam : " + requestParam);
    var url = protocol + '//' + host + '/user/proceed/generateRinCashOTP' + requestParam;
    if (window.XMLHttpRequest) {
        req = new XMLHttpRequest();
    } else if (window.ActiveXObject) {
        req = new ActiveXObject("Microsoft.XMLHTTP");
    }
    req.onreadystatechange = OTPResponse;
    req.open("POST", url, true);
    req.send(null);
}

function OTPResponse() {
    if (req.readyState == 4) {
        if (req.status == 200) {
            var data = req.responseText.trim();
            //alert(response);
            var response = data.trim().split("#@#");
            //alert(response[0].trim())
            if (response[0].trim() == 'sent') {
                //alert(response[1].trim())
                //$("#mobile-number").show();
                otpCount = response[1].trim();
                if (document.getElementById("otpConfirmationMessage") != null) {
                    document.getElementById("otpConfirmationMessageDiv").innerHTML = document.getElementById("otpConfirmationMessage").value;
                }
                document.getElementById("errorSpan").innerHTML = "";
                window.scrollTo(0, 0);
            }
            else if (response[0].trim() == 'error') {
                //$("#mobile-number").hide();
                document.getElementById("otpConfirmationMessageDiv").innerHTML = response[1].trim();
                window.scrollTo(0, 0);
            }

        }
    }
    //}

}

